import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { RxHamburgerMenu, RxCross2 } from "react-icons/rx"; // Modern icons from Radix UI

const NavBar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const menuItems = [
    { label: "Home", href: "#hero" },
    { label: "About", href: "#about" },
    { label: "Skills", href: "#skills" },
    { label: "Projects", href: "#projects" },
    { label: "Contact", href: "#contact" },
  ];

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  useEffect(() => {
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
      anchor.addEventListener("click", function (e) {
        e.preventDefault();
        const targetId = this.getAttribute("href");

        if (targetId === "#") {
          window.scrollTo({ top: 0, behavior: "smooth" });
        } else {
          const targetElement = document.querySelector(targetId);
          if (targetElement) {
            const headerOffset = document.querySelector("nav").offsetHeight;
            const elementPosition = targetElement.getBoundingClientRect().top + window.pageYOffset;
            const offsetPosition = elementPosition - headerOffset - 20;

            window.scrollTo({
              top: offsetPosition,
              behavior: "smooth",
            });
            setIsMobileMenuOpen(false);
          }
        }
      });
    });
  }, []);

  return (
    <nav className="fixed top-0 left-0 w-full bg-gray-900 bg-opacity-90 backdrop-blur-lg z-50 text-white shadow-lg">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center gap-3">
          <img
            src="https://placehold.co/40x40/2d3748/ffffff?text=SB"
            alt="Sarthak"
            className="w-10 h-10 rounded-full border-2 border-teal-400 shadow-md"
          />
          <span className="text-xl font-bold text-teal-400">Sarthak Bhuptani</span>
        </div>

        {/* Desktop Nav */}
        <ul className="hidden md:flex gap-8">
          {menuItems.map((item) => (
            <li key={item.label}>
              <a
                href={item.href}
                className="text-lg text-gray-300 hover:text-teal-400 font-medium transition-all duration-300 relative after:block after:w-0 after:h-[2px] after:bg-teal-400 after:transition-all hover:after:w-full after:mt-1"
              >
                {item.label}
              </a>
            </li>
          ))}
        </ul>

        {/* Mobile Toggle Button */}
        <button
          onClick={toggleMobileMenu}
          className="md:hidden focus:outline-none transition duration-300"
        >
          <motion.div
            initial={{ rotate: 0 }}
            animate={{ rotate: isMobileMenuOpen ? 90 : 0 }}
            transition={{ duration: 0.4 }}
          >
            {isMobileMenuOpen ? (
              <RxCross2 className="text-3xl text-teal-400" />
            ) : (
              <RxHamburgerMenu className="text-3xl text-teal-400" />
            )}
          </motion.div>
        </button>
      </div>

      {/* Mobile Menu Slide Down */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ y: "-100%", opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: "-100%", opacity: 0 }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
            className="md:hidden bg-gray-900 px-6 py-6 shadow-lg"
          >
            <ul className="flex flex-col gap-4">
              {menuItems.map((item, index) => (
                <motion.li
                  key={item.label}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 * index }}
                >
                  <a
                    href={item.href}
                    className="block text-lg text-gray-200 hover:text-teal-400 transition-colors duration-200"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    {item.label}
                  </a>
                </motion.li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
};

export default NavBar;
