import React, { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const menuItems = [
    { label: "Home", href: "#hero" },
    { label: "About", href: "#about" },
    { label: "Skills", href: "#skills" },
    { label: "Projects", href: "#projects" },
    { label: "Contact", href: "#contact" },
  ];

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-gray-900 bg-opacity-90 backdrop-blur-md text-white shadow-lg z-50">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center gap-3">
          <img
            src="https://placehold.co/40x40/2d3748/ffffff?text=SB"
            alt="Sarthak Bhuptani"
            className="w-10 h-10 rounded-full object-cover border-2 border-teal-400 shadow-md"
          />
          <span className="text-xl md:text-2xl font-bold text-teal-400">
            Sarthak Bhuptani
          </span>
        </div>

        {/* Desktop Menu */}
        <ul className="hidden md:flex gap-8">
          {menuItems.map((item) => (
            <li key={item.label}>
              <a
                href={item.href}
                className="text-lg text-gray-300 hover:text-teal-400 font-medium transition duration-300 transform hover:scale-105 relative
                           after:absolute after:left-0 after:bottom-0 after:h-[2px] after:w-0 after:bg-teal-400 after:transition-all after:duration-300
                           hover:after:w-full"
              >
                {item.label}
              </a>
            </li>
          ))}
        </ul>

        {/* Mobile Hamburger */}
        {/* Hamburger Toggle Button */}
        <div className="md:hidden">
          <motion.button
            onClick={toggleMobileMenu}
            className="relative w-8 h-8 flex flex-col justify-between items-center z-50 focus:outline-none"
            initial="closed"
            animate={isMobileMenuOpen ? "open" : "closed"}
            aria-label="Toggle menu"
          >
            <motion.span
              className="block w-8 h-1 bg-teal-400 rounded"
              variants={{
                closed: { rotate: 0, y: 0 },
                open: { rotate: 45, y: 8 },
              }}
              transition={{ duration: 0.3 }}
            />
            <motion.span
              className="block w-8 h-1 bg-teal-400 rounded"
              variants={{
                closed: { opacity: 1 },
                open: { opacity: 0 },
              }}
              transition={{ duration: 0.3 }}
            />
            <motion.span
              className="block w-8 h-1 bg-teal-400 rounded"
              variants={{
                closed: { rotate: 0, y: 0 },
                open: { rotate: -45, y: -8 },
              }}
              transition={{ duration: 0.3 }}
            />
          </motion.button>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ x: "-100%", opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: "-100%", opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="md:hidden absolute top-full left-0 w-full bg-gray-900 bg-opacity-95 backdrop-blur-md px-6 py-4 z-40 shadow-lg"
          >
            <ul className="flex flex-col gap-4">
              {menuItems.map((item, index) => (
                <motion.li
                  key={item.label}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.2, delay: index * 0.1 }}
                >
                  <a
                    href={item.href}
                    onClick={() => setIsMobileMenuOpen(false)}
                    className="block text-xl text-gray-200 hover:text-teal-400 transition-colors duration-200 py-2 px-3 rounded-md hover:bg-gray-800"
                  >
                    {item.label}
                  </a>
                </motion.li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
};

export default Navbar;
