import React from 'react';
import { Typewriter } from 'react-simple-typewriter';
import { motion } from 'framer-motion';

function Hero() {
    const accentColor = 'bg-teal-500'; 
    const accentTextColor = 'text-teal-400';

    return (
        <div
            id="hero"
            // Remove fixed pt-24 here if Navbar is fixed, to keep content centered
            className="min-h-screen pt-24 flex items-center justify-center px-4 md:px-12 bg-black text-white" 
        >
            {/* Main Grid Container */}
            <div className="max-w-7xl w-full grid grid-cols-1 md:grid-cols-2 gap-12 items-start">

                {/* LEFT COLUMN: Text Content (Unchanged) */}
                <motion.div
                    className="md:pt-10 md:pr-8" 
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.4 }}
                >
                    {/* ... Text content and CTAs ... (content here is unchanged from previous response) */}
                    <h1 className="text-4xl md:text-left md:text-5xl lg:text-6xl font-extrabold mb-6 leading-tight">
                        A <span className="text-gray-400">Web Developer</span> crafting
                        <span className={`block ${accentTextColor}`}>
                            seamless digital experiences.
                        </span>
                    </h1>
                    <p className="text-lg text-gray-400 max-w-xl mb-8 md:text-left">
                        Hi, I'm **Sarthak**! I am a{' '}
                        <span className="font-semibold text-teal-400">
                            <Typewriter
                                words={['Frontend Developer', 'React Enthusiast', 'Python Programmer']}
                                loop={0}
                                cursor
                                cursorStyle="_"
                                typeSpeed={70}
                                deleteSpeed={50}
                                delaySpeed={1500}
                            />
                        </span>
                        I translate complex business requirements into elegant, user-centered design solutions.
                    </p>
                    {/* ... CTAs and Social Icons ... (unchanged) */}
                    <div className="flex flex-wrap gap-4 mt-10 md:justify-start">
                        <a
                            href="#work"
                            className={`inline-block ${accentColor} text-black font-semibold py-3 px-6 rounded-lg shadow-lg hover:shadow-xl transition-transform duration-300 hover:scale-[1.02]`}
                        >
                            Discover My Work
                        </a>
                        <a
                            href="/Bhuptani Sarthak.pdf"
                            target="_blank"
                            rel="noopener noreferrer"
                            className={`inline-block border border-teal-500 ${accentTextColor} font-semibold py-3 px-6 rounded-lg hover:bg-teal-500/10 transition duration-300`}
                        >
                            <i className="fas fa-download mr-2"></i> Download CV
                        </a>
                    </div>
                    <div className="mt-8 pt-4 border-t border-gray-800 md:text-left">
                        <p className="text-gray-500 mb-2">Connect with me:</p>
                        <div className="flex gap-3 md:justify-start">
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">In</span>
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">Gh</span>
                        </div>
                    </div>
                </motion.div>


                {/* ➡️ RIGHT COLUMN: Illustration / Avatar Placeholder (FIXED FOR OVERFLOW) ➡️ */}
                <motion.div
                    className="relative flex justify-center md:justify-end **max-w-full**" // ADDED max-w-full
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.8, delay: 0.2 }}
                >
                    {/* Placeholder Avatar - Ensure it doesn't push its container too far */}
                    <div className="w-full **max-w-xs** h-96 bg-gray-900 rounded-xl border-4 border-teal-500/50 flex items-center justify-center p-6"> 
                        <span className="text-gray-600 text-xl font-medium">
                            [Custom Illustration Placeholder]
                        </span>
                    </div>

                    {/* Skill Tag 1: Adjusted positioning to be *inside* the main grid gap */}
                    <div className={`absolute top-1/4 **-left-4** px-3 py-1 text-sm font-semibold rounded-full ${accentColor} text-black hidden lg:block`}>
                        React Developer
                    </div>
                    {/* Skill Tag 2: Adjusted positioning to be *inside* the main grid gap */}
                    <div className={`absolute bottom-1/4 **-left-20** px-3 py-1 text-sm font-semibold rounded-full ${accentColor} text-black hidden lg:block`}>
                        Full Stack
                    </div>
                </motion.div>
            </div>
        </div>
    );
}

export default Hero;
