import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion'; // Import motion for animations

// Custom Typewriter component (retained from previous version)
const Typewriter = ({ words, loop = 0, typeSpeed = 70, deleteSpeed = 50, delaySpeed = 1500, cursorStyle = '_' }) => {
  const [text, setText] = useState('');
  const [wordIndex, setWordIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);

  useEffect(() => {
    let typer;
    const handleTyping = () => {
      const currentWord = words[wordIndex % words.length];
      const updatedText = isDeleting
        ? currentWord.substring(0, text.length - 1)
        : currentWord.substring(0, text.length + 1);

      setText(updatedText);

      if (!isDeleting && updatedText === currentWord) {
        // Done typing current word
        typer = setTimeout(() => setIsDeleting(true), delaySpeed);
      } else if (isDeleting && updatedText === '') {
        // Done deleting, move to next word
        setIsDeleting(false);
        setWordIndex(prevIndex => prevIndex + 1);
        if (loop !== 0 && (wordIndex + 1) % words.length === 0) {
          setLoopNum(prevNum => prevNum + 1);
        }
      }
    };

    if (loop === 0 || loopNum < loop) {
      const currentSpeed = isDeleting ? deleteSpeed : typeSpeed;
      typer = setTimeout(handleTyping, currentSpeed);
    }

    return () => clearTimeout(typer);
  }, [text, isDeleting, wordIndex, words, typeSpeed, deleteSpeed, delaySpeed, loop, loopNum]);

  return (
    <>
      {text}
      <span className="cursor">{cursorStyle}</span>
    </>
  );
};


function Hero() {
  // Framer Motion variants for staggered animations
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15, // Slightly faster stagger
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: -20 }, // Slide in from slightly above
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: "tween", // Use tween for a smoother, less bouncy feel
        ease: "easeOut",
        duration: 0.6,
      },
    },
  };

  const statItemVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        type: "spring",
        damping: 12,
        stiffness: 150,
      },
    },
  };

  return (
    <div
      id="hero" // Ensure this ID matches your navbar's link
      className="relative pt-24 min-h-screen flex flex-col items-center justify-center px-4 overflow-hidden
                 bg-cover bg-center bg-fixed" // Added background image classes
      style={{ backgroundImage: `url('https://images.unsplash.com/photo-1517694712202-14dd9538aa97?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')` }} // Placeholder image
    >
      {/* Dark overlay for readability */}
      <div className="absolute inset-0 bg-black opacity-70 z-0"></div>

      {/* Hero Content */}
      <motion.div
        className="z-10 text-center max-w-4xl mx-auto py-12 px-6 bg-gray-900 bg-opacity-70 rounded-3xl shadow-2xl border border-gray-700 backdrop-blur-sm"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.h1
          className="text-5xl md:text-7xl font-extrabold mb-4 leading-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-400 to-blue-400"
          variants={itemVariants}
        >
          Hi, I'm Sarthak
        </motion.h1>
        <motion.h2
          className="text-2xl md:text-4xl font-semibold text-gray-300 mb-6"
          variants={itemVariants}
        >
          I am a{' '}
          <span className="text-purple-400"> {/* Changed typewriter color */}
            <Typewriter
              words={['Frontend Developer', 'React Enthusiast', 'Python Programmer', 'FullStack Developer']}
              loop={0} // Loop indefinitely
              cursor
              cursorStyle="_"
              typeSpeed={70}
              deleteSpeed={50}
              delaySpeed={1500}
            />
          </span>
        </motion.h2>

        {/* Short Summary */}
        <motion.p
          className="text-lg md:text-xl text-gray-400 mt-6 max-w-2xl mx-auto"
          variants={itemVariants}
        >
          Hi, Iâ€™m Sarthak â€” I design and build modern websites that load fast and look sharp. Letâ€™s bring your ideas to life.
        </motion.p>

        {/* Quick Stats */}
        <motion.div
          className="mt-8 flex flex-wrap justify-center gap-4 md:gap-6 text-gray-300 text-base md:text-lg font-medium"
          variants={containerVariants} // Use container variants for stagger
          initial="hidden"
          animate="visible"
        >
          <motion.div variants={statItemVariants} className="flex items-center gap-2 bg-gray-800 px-5 py-2 rounded-full shadow-md border border-gray-700 hover:scale-105 transition-transform duration-200">
            <span role="img" aria-label="rocket">ðŸš€</span> 3+ Projects
          </motion.div>
          <motion.div variants={statItemVariants} className="flex items-center gap-2 bg-gray-800 px-5 py-2 rounded-full shadow-md border border-gray-700 hover:scale-105 transition-transform duration-200">
            <span role="img" aria-label="books">ðŸ“š</span> Final Year Student
          </motion.div>
          <motion.div variants={statItemVariants} className="flex items-center gap-2 bg-gray-800 px-5 py-2 rounded-full shadow-md border border-gray-700 hover:scale-105 transition-transform duration-200">
            <span role="img" aria-label="briefcase">ðŸ’¼</span> Open to Work
          </motion.div>
        </motion.div>

        {/* Resume Button */}
        <motion.div className="mt-10" variants={itemVariants}>
          <a
            href="/Resume.pdf" // Ensure this path is correct for your hosted resume
            target="_blank"
            rel="noopener noreferrer"
            className="inline-block bg-gradient-to-r from-teal-500 to-blue-500 text-gray-900 font-bold py-3 px-10 rounded-full shadow-xl
                       transition duration-300 transform hover:scale-105 hover:shadow-2xl focus:outline-none focus:ring-4 focus:ring-teal-500 focus:ring-opacity-50
                       button-hover-effect"
          >
            View Resume
          </a>
        </motion.div>
      </motion.div>

      {/* Custom CSS for Hero Section Animations */}
      <style>{`
        .button-hover-effect {
            position: relative;
            overflow: hidden;
            z-index: 1;
        }
        .button-hover-effect::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
            transition: all 0.5s ease;
            z-index: -1;
        }
        .button-hover-effect:hover::before {
            left: 100%;
        }


        /* Cursor blinking for typewriter effect */
        .cursor {
          display: inline-block;
          animation: blink-caret 0.75s step-end infinite;
          border-right: 2px solid white; /* Ensure cursor is visible */
        }

        @keyframes blink-caret {
          from, to { border-right-color: transparent }
          50% { border-right-color: white; } /* Adjust color as needed */
        }
      `}</style>
    </div>
  );
}

export default Hero;
