import React, { useState, useEffect } from "react";
import profile from "../assets/profile.jpg";

const NavBar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(true); // default dark

  const toggleMenu = () => setIsOpen(!isOpen);
  const toggleTheme = () => setDarkMode(!darkMode);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const menuItems = [
    { label: "Home", href: "#home" },
    { label: "About", href: "#about" },
    { label: "Skills", href: "#skills" },
    { label: "Projects", href: "#projects" },
    { label: "Contact", href: "#contact" },
  ];

  return (
    <nav className="fixed top-0 left-0 w-full bg-white dark:bg-black text-black dark:text-white shadow-lg z-50 transition duration-300">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Left Side: Logo */}
        <div className="flex items-center gap-2">
          <img src={profile} alt="Sarthak" className="w-6 h-6 rounded-full object-cover" />
          <span className="text-lg font-bold">Sarthak</span>
        </div>

        {/* Right Side: Toggle + Hamburger */}
        <div className="flex items-center gap-4 lg:hidden">
          {/* Dark Mode Toggle */}
          <button
            onClick={toggleTheme}
            className="text-xl hover:text-blue-500 transition"
            aria-label="Toggle Theme"
          >
            {darkMode ? "ðŸŒž" : "ðŸŒ™"}
          </button>

          {/* Hamburger */}
          <button onClick={toggleMenu} className="focus:outline-none" aria-label="Toggle Menu">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              {isOpen ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              )}
            </svg>
          </button>
        </div>

        {/* Desktop Menu */}
        <ul className="hidden lg:flex gap-6 items-center">
          {menuItems.map((item) => (
            <li key={item.label}>
              <a href={item.href} className="hover:text-blue-400 transition duration-200">
                {item.label}
              </a>
            </li>
          ))}

          {/* Desktop Theme Toggle */}
          <li>
            <button
              onClick={toggleTheme}
              className="text-xl hover:text-blue-500 transition"
              aria-label="Toggle Theme"
            >
              {darkMode ? "ðŸŒž" : "ðŸŒ™"}
            </button>
          </li>
        </ul>
      </div>

      {/* Mobile Slide Menu */}
      <div
        className={`lg:hidden transform transition-transform duration-300 ease-in-out ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        } absolute top-16 left-0 w-full bg-white dark:bg-black px-6 py-4`}
      >
        <ul className="flex flex-col gap-4">
          {menuItems.map((item) => (
            <li key={item.label}>
              <a
                href={item.href}
                onClick={() => setIsOpen(false)}
                className="block hover:text-blue-400 transition"
              >
                {item.label}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default NavBar;
