import React from 'react';
import { Typewriter } from 'react-simple-typewriter';
import { motion } from 'framer-motion';

function Hero() {
    // Define the bright accent color for consistency
    const accentColor = 'bg-teal-500'; 
    const accentTextColor = 'text-teal-400';

    return (
        <div
            id="hero"
            className="min-h-screen pt-24 flex items-center justify-center px-4 md:px-12 bg-black text-white"
        >
            {/* Main Grid Container: Illustration on Left, Content on Right */}
            <div className="max-w-7xl w-full grid grid-cols-1 md:grid-cols-2 gap-12 items-start">

                {/* LEFT COLUMN: Illustration / Avatar Placeholder */}
                <motion.div
                    className="relative flex justify-center md:justify-start"
                    initial={{ opacity: 0, x: -50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.8, delay: 0.2 }}
                >
                    {/* Placeholder for the custom illustration/avatar */}
                    <div className="w-full max-w-sm h-96 bg-gray-900 rounded-xl border-4 border-teal-500/50 flex items-center justify-center p-6">
                        <span className="text-gray-600 text-xl font-medium">
                            [Custom Illustration Placeholder]
                        </span>
                    </div>

                    {/* Example Skill Tags - Positioned like the reference image */}
                    <div className={`absolute top-1/4 -right-10 px-3 py-1 text-sm font-semibold rounded-full ${accentColor} text-black hidden lg:block`}>
                        React Developer
                    </div>
                    <div className={`absolute bottom-1/4 left-0 px-3 py-1 text-sm font-semibold rounded-full ${accentColor} text-black hidden lg:block`}>
                        Full Stack
                    </div>
                </motion.div>

                {/* RIGHT COLUMN: Text Content and CTAs */}
                <motion.div
                    className="md:pt-10 md:pl-8"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.4 }}
                >
                    {/* Main Title - Sarthak's expertise (Like the reference image's main H1) */}
                    <h1 className="text-4xl md:text-5xl lg:text-6xl font-extrabold mb-6 leading-tight">
                        A <span className="text-gray-400">Web Developer</span> crafting
                        <span className={`block ${accentTextColor}`}>
                            seamless digital experiences.
                        </span>
                    </h1>

                    {/* Typewriter is now secondary text / self-introduction */}
                    <p className="text-lg text-gray-400 max-w-xl mb-8">
                        Hi, I'm **Sarthak**! I am a{' '}
                        <span className="font-semibold text-teal-400">
                            <Typewriter
                                words={['Frontend Developer', 'React Enthusiast', 'Python Programmer']}
                                loop={0}
                                cursor
                                cursorStyle="_"
                                typeSpeed={70}
                                deleteSpeed={50}
                                delaySpeed={1500}
                            />
                        </span>
                        I translate complex business requirements into elegant, user-centered design solutions.
                    </p>

                    {/* CTA Buttons - Matching the look with different styles */}
                    <div className="flex flex-wrap gap-4 mt-10">
                        {/* 1. Primary CTA: Solid Teal Fill, Black Text (like "Discover My Work") */}
                        <a
                            href="#work"
                            className={`inline-block ${accentColor} text-black font-semibold py-3 px-6 rounded-lg shadow-lg hover:shadow-xl transition-transform duration-300 hover:scale-[1.02]`}
                        >
                            Discover My Work
                        </a>
                        
                        {/* 2. Secondary CTA: Outline (like "Download CV") */}
                        <a
                            href="/Bhuptani Sarthak.pdf"
                            target="_blank"
                            rel="noopener noreferrer"
                            className={`inline-block border border-teal-500 ${accentTextColor} font-semibold py-3 px-6 rounded-lg hover:bg-teal-500/10 transition duration-300`}
                        >
                            <i className="fas fa-download mr-2"></i> Download CV
                        </a>
                    </div>

                    {/* Connect with me (Social Icons Placeholder) */}
                    <div className="mt-8 pt-4 border-t border-gray-800">
                        <p className="text-gray-500 mb-2">Connect with me:</p>
                        <div className="flex gap-3">
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">In</span>
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">Gh</span>
                        </div>
                    </div>

                </motion.div>
            </div>
        </div>
    );
}

export default Hero;
