import React from 'react';
import { Typewriter } from 'react-simple-typewriter';
import { motion } from 'framer-motion';

function Hero() {
    const accentColor = 'bg-teal-500'; 
    const accentTextColor = 'text-teal-400';

    return (
        <div
            id="hero"
            className="min-h-screen pt-24 flex flex-col items-center justify-center px-4 md:px-12 bg-black text-white"
        >
            <div className="max-w-4xl w-full text-center">

                {/* 1. AVATAR / ILLUSTRATION (Top Center) */}
                <motion.div
                    className="mx-auto mb-10 w-40 h-40 bg-gray-900 rounded-full border-4 border-teal-500 flex items-center justify-center"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.6, type: 'spring' }}
                >
                    <span className="text-gray-600 text-lg font-medium">Avatar</span>
                </motion.div>

                {/* 2. MAIN TITLE / HEADING */}
                <motion.h1
                    className="text-4xl md:text-6xl font-extrabold mb-6 leading-tight"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.2 }}
                >
                    A <span className="text-gray-400">Web Developer</span> crafting
                    <span className={`block ${accentTextColor}`}>
                        seamless digital experiences.
                    </span>
                </motion.h1>

                {/* 3. INTRO PARAGRAPH (Typewriter included) */}
                <motion.p
                    className="text-lg text-gray-400 max-w-2xl mx-auto mb-10"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.4 }}
                >
                    Hi, I'm **Sarthak**! I am a{' '}
                    <span className="font-semibold text-teal-400">
                        <Typewriter
                            words={['Frontend Developer', 'React Enthusiast', 'Python Programmer']}
                            loop={0}
                            cursor
                            cursorStyle="_"
                            typeSpeed={70}
                            deleteSpeed={50}
                            delaySpeed={1500}
                        />
                    </span>
                    I translate complex business requirements into elegant, user-centered design solutions.
                </motion.p>

                {/* 4. CTA Buttons & Social Links */}
                <motion.div
                    className="flex flex-col items-center gap-4 mt-6"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.6 }}
                >
                    {/* CTA Buttons */}
                    <div className="flex flex-wrap justify-center gap-4">
                        {/* Primary CTA: Discover My Work */}
                        <a
                            href="#work"
                            className={`inline-block ${accentColor} text-black font-semibold py-3 px-6 rounded-lg shadow-lg hover:shadow-xl transition-transform duration-300 hover:scale-[1.02]`}
                        >
                            Discover My Work
                        </a>
                        
                        {/* Secondary CTA: Download CV */}
                        <a
                            href="/Bhuptani Sarthak.pdf"
                            target="_blank"
                            rel="noopener noreferrer"
                            className={`inline-block border border-teal-500 ${accentTextColor} font-semibold py-3 px-6 rounded-lg hover:bg-teal-500/10 transition duration-300`}
                        >
                            <i className="fas fa-download mr-2"></i> Download CV
                        </a>
                    </div>

                    {/* Social Icons */}
                    <div className="mt-8 pt-4 border-t border-gray-800 w-full max-w-sm mx-auto">
                        <p className="text-gray-500 mb-2">Connect with me:</p>
                        <div className="flex gap-3 justify-center">
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">In</span>
                            <span className="w-8 h-8 flex items-center justify-center bg-gray-800 rounded-lg hover:bg-teal-500 transition cursor-pointer">Gh</span>
                        </div>
                    </div>
                </motion.div>
            </div>
        </div>
    );
}

export default Hero;
